import React, { useState, useRef, useEffect } from "react";
import "./MusicPlayer.css";
import { FaCloudUploadAlt, FaCog, FaSearch, FaHeart, FaBackward, FaPlay, FaPause, FaForward, FaEllipsisH } from "react-icons/fa";
import { IoMdClose } from "react-icons/io";
import { useNavigate } from "react-router-dom";

const songs = [
  { id: 1, 
    title: "Watermelon Sugar",
    artist: "Harry Styles", 
    img: "data:image/jpeg;base64,", 
    src: `${process.env.PUBLIC_URL}/assets/song1.mp3` 
  },
  { id: 2, 
    title: "Dark Side", 
    artist: "Alan Walker", 
    img: "https://i1.sndcdn.com/artworks-000474812505-rg5kxv-t500x500.jpg", 
    src: `${process.env.PUBLIC_URL}/assets/song2.mp3` 
  },
  { id: 3, 
    title: "Kill The Love", artist: "Black Pink", img: "data:image/jpeg;base64,", 
    src: `${process.env.PUBLIC_URL}/assets/song3.mp3` 
  },
  { id: 4, 
    title: "Anyone", 
    artist: "Justin Bieber", 
    img: "data:image/jpeg;base64,", src: `${process.env.PUBLIC_URL}/assets/song4.mp3` 
  },
  { id: 5, 
    title: "Hold On", 
    artist: "Justin", 
    img: "data:image/jpeg;base64,", src: `${process.env.PUBLIC_URL}/assets/song5.mp3` 
  },
  { id: 6, 
    title: "Blinding Lights", 
    artist: "The Weeknd", 
    img: "data:image/jpeg;base64,", src: `${process.env.PUBLIC_URL}/assets/song6.mp3` 
  },
  { id: 7, 
    title: "Levitating", 
    artist: "Dua Lipa", 
    img: "data:image/jpeg;base64,", 
    src: `${process.env.PUBLIC_URL}/assets/song7.mp3` },
  { id: 8, 
    title: "Shape of You", 
    artist: "Ed Sheeran", 
    img: "data:image/jpeg;base64,", 
    src: `${process.env.PUBLIC_URL}/assets/song8.mp3` 
  },
  { id: 9, 
    title: "Senorita", 
    artist: "Shawn Mendes", 
    img: "data:image/jpeg;base64,", src: `${process.env.PUBLIC_URL}/assets/song9.mp3` 
  },
  { id: 10, 
    title: "Believer", 
    artist: "Imagine Dragons", 
    img: "data:image/jpeg;base64,", 
    src: `${process.env.PUBLIC_URL}/assets/song10.mp3` 
  },
];

const MusicPlayer = () => {
  const navigate = useNavigate();

  const [currentSongIndex, setCurrentSongIndex] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [likedIds, setLikedIds] = useState([]);
  const [activeTab, setActiveTab] = useState("All");

  const audioRef = useRef(null);
  
  const filteredList = (() => {
    if (activeTab === "All") return songs;
    if (activeTab === "Liked") return songs.filter(s => likedIds.includes(s.id));
    if (activeTab === "Popular") return songs.slice(0, 5); 
    if (activeTab === "New") return songs.slice(-5).reverse(); 
    return songs;
  })();

  const currentSong = songs[currentSongIndex];

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    const handleLoaded = () => setDuration(audio.duration || 0);
    const handleTime = () => setCurrentTime(audio.currentTime || 0);
    const handleEnded = () => {
      nextSong(true);
    };

    audio.addEventListener("loadedmetadata", handleLoaded);
    audio.addEventListener("timeupdate", handleTime);
    audio.addEventListener("ended", handleEnded);

    return () => {
      audio.removeEventListener("loadedmetadata", handleLoaded);
      audio.removeEventListener("timeupdate", handleTime);
      audio.removeEventListener("ended", handleEnded);
    };
  }, []);

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    audio.pause();
    audio.load();
    setCurrentTime(0);
    setDuration(0);

    if (isPlaying) {
      const p = audio.play();
      if (p && p.catch) p.catch(() => setIsPlaying(false));
    }
  }, [currentSongIndex]);

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    if (isPlaying) {
      audio.play().catch((err) => {
  
        console.warn("Play prevented:", err);
        setIsPlaying(false);
      });
    } else {
      audio.pause();
    }
  }, [isPlaying]);

  const formatTime = (time) => {
    if (!time || isNaN(time)) return "00:00";
    const mins = Math.floor(time / 60);
    const secs = Math.floor(time % 60);
    return `${mins}:${secs < 10 ? "0" + secs : secs}`;
  };

  const handlePlayPause = () => {
    if (!audioRef.current) return;

    if (!isPlaying) {
      audioRef.current.play()
        .then(() => setIsPlaying(true))
        .catch((err) => {
          console.log("play error", err);
          setIsPlaying(false);
        });
    } else {
      audioRef.current.pause();
      setIsPlaying(false);
    }
  };

  const handleSliderChange = (e) => {
    const val = Number(e.target.value);
    if (!audioRef.current) return;
    audioRef.current.currentTime = val;
    setCurrentTime(val);
  };

  const prevSong = () => {
    setCurrentSongIndex((prev) => (prev === 0 ? songs.length - 1 : prev - 1));
    setIsPlaying(true);
  };

  const nextSong = (auto = false) => {
    setCurrentSongIndex((prev) => (prev + 1) % songs.length);
    if (!auto) setIsPlaying(true);
  };

  const toggleLike = (song) => {
    setLikedIds((prev) => {
      const exists = prev.includes(song.id);
      const next = exists ? prev.filter(id => id !== song.id) : [...prev, song.id];
      if (!exists) setActiveTab("Liked");
      return next;
    });
  };

  const handleSongClick = (index) => {
    setCurrentSongIndex(index);
    setIsPlaying(true); 
  };

  return (
    <div className="music-app">
      <header className="top-header">
        <h2>MUSICVERSE</h2>
        <div className="header-icons">
          <FaCloudUploadAlt className="icon" onClick={() => navigate("/Upload")} />
          <FaSearch className="icon" />
          <FaCog className="icon"  onClick={()=>navigate("/setting")} />
        </div>
      </header>

      <div className="tabs">
        {["All", "Liked", "Popular", "New"].map(tab => (
          <button
            key={tab}
            className={"tab-btn " + (activeTab === tab ? "active" : "")}
            onClick={() => setActiveTab(tab)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="main-layout">
        <div className="song-list">
          {filteredList.length === 0 ? (
            <p className="empty">No songs in this list</p>
          ) : (
            filteredList.map((song) => {
              const idx = songs.findIndex(s => s.id === song.id);
              return (
                <div className="song-row" key={song.id} onClick={() => handleSongClick(idx)}>
                  <img src={song.img} alt={song.title} />
                  <div className="meta">
                    <p className="song-title">{song.title}</p>
                    <p className="song-artist">{song.artist}</p>
                  </div>
                  <button
                    className={"like-btn " + (likedIds.includes(song.id) ? "liked" : "")}
                    onClick={(e) => { e.stopPropagation(); toggleLike(song); }}
                    aria-label={likedIds.includes(song.id) ? "Unlike" : "Like"}
                  >
                    <FaHeart />
                  </button>
                </div>
              );
            })
          )}
        </div>

        <div className="player-card">
          <IoMdClose className="close-btn" />

          <img src={currentSong.img} className="player-img" alt="current" />

          <p className="song-title player-title">{currentSong.title}</p>
          <p className="song-artist">{currentSong.artist}</p>

          <audio ref={audioRef} src={currentSong.src} preload="metadata"></audio>

          <input type="range"
            className="slider"
            min="0"
            max={duration || 0}
            value={currentTime}
            onChange={handleSliderChange}
            step="0.1"
          />

          <div className="time-bar">
            <span>{formatTime(currentTime)}</span>
            <span>{formatTime(duration)}</span>
          </div>

          <div className="player-controls">
            <button className="ctrl">
              <FaHeart className={likedIds.includes(currentSong.id) ? "liked" : ""} onClick={() => toggleLike(currentSong)} />
            </button>
            <button className="ctrl" onClick={prevSong}><FaBackward /></button>
            <button className="ctrl play" onClick={handlePlayPause}>
              {isPlaying ? <FaPause /> : <FaPlay />}
            </button>
            <button className="ctrl" onClick={() => nextSong(false)}><FaForward /></button>
            <button className="ctrl"><FaEllipsisH /></button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MusicPlayer;
